import React, {useState, useEffect, useReducer} from 'react';
import logo from './logo.svg';
import './App.css';
import * as services from './services';
import LoginPane from './LoginPane';
import Posts from './Posts';
import Error from './Error';
import SignupPane from './SignupPane';



const initialState = {
  isLoggedIn: false,
  showSignup: false,
  userName: '',
  error: '',
  posts: []
};

const reducer = (state, action) => {
  switch(action.type){
    case 'LOGIN_SUCCESS':
      return {...state, isLoggedIn: true};
    case 'LOGIN_FAILED':
      return {...state, isLoggedIn: false, error: action.payload.message};
    case 'POSTS_FETCHED':
      return {...state, posts: action.payload.data}
    case 'ERR_FOUND':
      return {...state, error: action.payload}
    case 'TOGGLE_SIGNUP':
      return {...state, showSignup: action.payload}
    default:
      return state;
  }
}


function App() {
  // const [isLoading, setIsLoading] = useState(true);
  const [state, dispatch] = useReducer(reducer, initialState);
  // const [posts, setPosts] = useState([]);

//   const callSignUpService = (username, password) => {
//   services.fetchLogIn(username, password)
//   .then((response) => {
//     dispatch({type: 'LOGIN_SUCCESS'},)
//   })
//   .catch((e) => {

//   })
// }

const fetchAllPosts = () => {
  services.fetchAllUserPosts()
  .then(response => {
    // setPosts(response.data);
    dispatch({type: 'POSTS_FETCHED', payload: response})
  })
  .catch(err => {
    console.log(err);
    //set error message
    dispatch({type: 'ERROR_FOUND', payload: err})
  });
};

  useEffect(() => {
    services.fetchLoginStatus()
    .then((response) => {
      console.log("You are logged in!");
      dispatch({type: 'LOGIN_SUCCESS', payload: response})
      fetchAllPosts();
    })
    .catch((err) => {
      console.log(err.message); //"invalid session"
      dispatch({type: 'LOGIN_FAILED', payload: err});
    });
  }, []);

  return (
    <div className="App">
      <div className = "Content">

        {state.error? <Error err = {state.error}/> : ''}
        {state.showSignup? <SignupPane dispatch = {dispatch({type: 'TOGGLE_SIGNUP', payload: true})}/> : state.isLoggedIn? '' : <LoginPane/>}
        {state.showSignup? <SignupPane/> : state.isLoggedIn? <Posts postsToDisplay = {state.posts}/> : ''}

      </div>
    </div>
  );
}

export default App;
