import React, {useContext} from 'react';
import logo from './logo.svg';
import {StateContext} from './App';

function LoginPane(){
    const context = useContext(StateContext);
    const handleClick = () => {
        context.stateDispatch({type: 'TOGGLE_SIGNUP', payload: true});
    };
    return(
        <div className = 'login-card'>
            <img src = {logo} alt = "login-logo" className = "App-logo"/>
            <div>
                <span>Username</span><input placeholder = "username"></input><br/><br/>
                <span>Password  </span><input type = "password" placeholder = "password"></input><br/><br/>
                <button onClick = {handleClick}>LOGIN</button><br/><br/>
                <label>Don't have an account yet?</label><button>SIGNUP</button><br/><br/><br/>
            </div>
        </div>
    )
}




export default LoginPane;
