import React, {useState, useEffect, useReducer} from 'react';
import logo from './logo.svg';
import './App.css';
import * as services from './services';
import LoginPane from './LoginPane';
import Posts from './Posts';



const initialState = {
  isLoggedIn: false,
  userName: '',
  error: '',
  isLoading: true,
  posts: []
};

const reducer = (state, action) => {
  switch(action.type){
    case 'LOGIN_SUCCESS':
      return {...state, isLoggedIn: true, userName: action.payload.data};
    case 'LOGIN_FAILED':
      return {...state, isLoggedIn: false, userName: '', error: action.payload.message};
    case 'POSTS_FETCHED':
      return {...state, posts: action.payload.data}
    case 'ERR_FOUND':
      return {...state, error: action.payload}
    case 'SET_LOADING':
      return {...state, error: action.payload}
    default:
      return state;
  }
}


function App() {
  // const [isLoading, setIsLoading] = useState(true);
  const [state, dispatch] = useReducer(reducer, initialState);
  // const [posts, setPosts] = useState([]);

//   const callSignUpService = (username, password) => {
//   services.fetchLogIn(username, password)
//   .then((response) => {
//     dispatch({type: 'LOGIN_SUCCESS'},)
//   })
//   .catch((e) => {

//   })
// }

const fetchAllPosts = () => {
  services.fetchAllUserPosts()
  .then(response => {
    // setPosts(response.data);
    dispatch({type: 'POSTS_FETCHED', payload: response})
  })
  .catch(err => {
    console.log(err);
    //set error message
    dispatch({type: 'ERROR_FOUND', payload: err})
  });
};

  useEffect(() => {
    dispatch({type: 'SET_LOADING', payload: true});
    fetchAllPosts();
    // services.fetchLoginStatus()
    // .then((response) => {
    //   console.log("You are logged in!");
    //   dispatch({type: 'LOGIN_SUCCESS', payload: response})
    //   setIsLoading(false);
    // })
    // .catch((err) => {
    //   console.log(err.message); //"invalid session"
    //   dispatch({type: 'LOGIN_FAILED', payload: err});
    //   setIsLoading(false);
    // });
    dispatch({type: 'SET_LOADING', payload: false});
  }, []);

  return (
    <div className="App">
      {state.isLoading? 
      <div className = "Background">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>
      </div> : ''
      }

      <div className = "Content">
        {/* {state.isLoggedIn? '' : <LoginPane/>} */}
        {/* {state.error? state.error : ''} */}
        <Posts postsToDisplay = {state.posts}/>
      </div>
    </div>
  );
}

export default App;
